            token: token,
            maxAmount: maxAmount,
            spentAmount: 0,
            validUntil: validUntilTime
        });

        emit SpendingLimitSet(delegationHash, token, maxAmount, validUntilTime);
    }

    /**
     * @dev Sets allowed target for a delegation
     * Anyone can set targets for their own delegations
