            maxRolls: maxRolls,
            maxBuys: maxBuys,
            maxRails: maxRails,
            maxFaucets: maxFaucets,
            maxCooks: maxCooks,
            usedRolls: 0,
            usedBuys: 0,
            usedRails: 0,
            usedFaucets: 0,
            usedCooks: 0,
            validUntil: validUntilTime
